import React, { Component, Fragment } from "react";
import Popup from "reactjs-popup";
import { LinkContainer } from "react-router-bootstrap";
import ReactPaginate from "react-paginate";
import "../../../../styles/buttons.css";
import "./scss/search.scss";
import "react-table/react-table.css";
import { Card, ProgressBar } from "react-bootstrap";
import { UserIDContext } from "../../MainPagesRoutingContainer/index.tsx";
import swal from "sweetalert";
import { UserEmailContext } from "../../MainPagesRoutingContainer/index";
//range slider
const Modal = ({
  hugID,
  hugName,
  hugAmount,
  hugDescription,
  hugCategory,
  medicalCategory,
  location,
  ages,
  hugDeadline,
  handleChange,
  handleDonate,
  handleFundingStatus,
  f
}) => {
  //percent of amount raised
  let percentRaised = Math.round((f / hugAmount) * 100);
  //if percent is a small fraction
  if (percentRaised < 1) {
    percentRaised = 1;
  }
  //if hug percent is fulfilled
  if (percentRaised > 100) {
    percentRaised = 100;
  }

  const progressInstance = (
    <ProgressBar
      variant="success"
      now={percentRaised}
      label={`${percentRaised}%`}
    />
  );

  return (
    // modal trigger
    <Popup
      trigger={
        <button
          className="submit-button"
          data-toggle="modal"
          data-target="#donateModal"
          onClick
        >
          Donate
        </button>
      }
      modal
    >
      {/*//to close modal*/}
      {close => (
        <div className="row" id="donateModal">
          <div className="col text-center">
            <a className="closeModal" onClick={close}>
              &times;
            </a>
            <h1>
              {" "}
              {hugName}: ${hugAmount}
            </h1>
            <div className="form-group-row">
              <label>Funding Status: Raised ${f}</label>
              {/*range slider here*/}

              <div className="form-group row">
                <label className="col-2 col-form-label" />
                $0
                <div className="col-8">{progressInstance}</div>${hugAmount}
              </div>
            </div>
            <div>
              <p>Description: {hugDescription}</p>
              <p>HUG Category: {hugCategory}</p>
              <p>Medical Category: {medicalCategory}</p>
              <p>Location: {location}</p>
              <p>Ages: {ages}</p>
              <p>Deadline: {hugDeadline}</p>
            </div>
            <h2>How much would you like to donate?</h2>

            <div className="form-group row">
              <div className="col-1" />
              <label htmlFor="donate" className="col-2 col-form-label">
                Donation Amount $
              </label>
              <div className="col-8">
                <input
                  type="text"
                  className="form-control"
                  id="donateAmount"
                  onChange={handleChange("donateAmount")}
                />
              </div>
              <div className="col-1" />
            </div>
            <LinkContainer to="/search">
              <button
                className="submit-button"
                onClick={() => handleDonate(hugID)}
              >
                Submit
              </button>
            </LinkContainer>
            {/*close modal*/}
            <LinkContainer to="/search">
              <button
                className="submit-button"
                onClick={() => {
                  close();
                }}
              >
                Cancel
              </button>
            </LinkContainer>
          </div>
        </div>
      )}
    </Popup>
  );
};

const FormGroup = ({ labelName, value, handleChange }) => {
  return (
    <div className="form-group row">
      <div className="col-1" />
      <label
        htmlFor={labelName.replace(/\s+/g, "-")}
        className="col-2 col-form-label"
      >
        {labelName}
      </label>
      <div className="col-8">
        <input
          type="text"
          className="form-control"
          id={labelName.replace(/\s+/g, "-")}
          value={value}
          onChange={handleChange}
        />
      </div>
      <div className="col-1" />
    </div>
  );
};

//user id
let userID = 0;

//user email
let userEmail = "";

// to filter the search card results
const FilterFormGroup = ({
  hugCategory,
  medicalCategory,
  specialtyCare,
  location,
  age,
  handleChange,
  hugOptions,
  medOptions,
  specialtyOptions,
  orderBy
}) => {
  return (
    <Fragment>
      <div className="col">
        <div className="col-1" />

        <div className="form-group row">
          <div className="col-1" />
          <label htmlFor="hugCategory" className="col-2 col-form-label">
            HUG Category
          </label>
          <div className="col-8">
            <select
              id="hugCategory"
              placeholder="Select..."
              value={hugCategory}
              className="form-control"
              onChange={handleChange("hugCategory")}
            >
              <option value="" disabled selected>
                Select HUG Category...
              </option>
              {hugOptions}
            </select>
          </div>
        </div>

        <div className="form-group row">
          <div className="col-1" />
          <label htmlFor="medicalCategory" className="col-2 col-form-label">
            Medical Category
          </label>
          <div className="col-8">
            <select
              id="medicalCategory"
              className="form-control"
              type="medicalCategory"
              value={medicalCategory}
              onChange={handleChange("medicalCategory")}
            >
              <option value="" disabled selected>
                Select Medical Category...
              </option>
              {medOptions}
            </select>
          </div>
        </div>

        <div className="form-group row">
          <div className="col-1" />
          <label htmlFor="Specialty Care" className="col-2 col-form-label">
            Specialty Care
          </label>
          <div className="col">
            <select
              id="specialtyCare"
              value={specialtyCare}
              className="form-control"
              onChange={handleChange("specialtyCare")}
              disabled={medicalCategory !== "Specialty Care"}
            >
              <option value="" disabled selected>
                Select Specialty Care...
              </option>
              {specialtyOptions}
            </select>
          </div>
          <div className="col-1" />
        </div>

        <div className="form-group row">
          <div className="col-1" />
          <label htmlFor="location" className="col-2 col-form-label">
            Location
          </label>
          <div className="col-8">
            <select
              id="location"
              placeholder="Select Location..."
              value={location}
              className="form-control"
              onChange={handleChange("location")}
            >
              <option value="" disabled selected>
                Select Location...
              </option>
              <option value="Alabama">Alabama</option>
              <option value="AK">Alaska</option>
              <option value="AZ">Arizona</option>
              <option value="AR">Arkansas</option>
              <option value="CA">California</option>
              <option value="CO">Colorado</option>
              <option value="CT">Connecticut</option>
              <option value="DE">Delaware</option>
              <option value="DC">District Of Columbia</option>
              <option value="FL">Florida</option>
              <option value="GA">Georgia</option>
              <option value="HI">Hawaii</option>
              <option value="ID">Idaho</option>
              <option value="IL">Illinois</option>
